<?phpnamespace StudentsApp\Repositories;use PDO;use PDOException;use StudentsApp\Utilities\RedirectTrait;class DatabaseConnection implements ConnectionInterface{    use RedirectTrait;    private $connection;    public function __construct($dataSourceName, $databaseUsername, $databasePassword)     {      try {              $this->connection = new PDO($dataSourceName,                                   $databaseUsername,                                   $databasePassword,                                   [PDO::ATTR_ERRMODE => PDO::ERRMODE_EXCEPTION] );          }catch(PDOException $pdoException) {              error_log("Exception occurred ". $pdoException->getMessage());              $this->redirectTo("errortechnical");              exit;          }      }    public function getConnection()     {          if($this->connection instanceof PDO) {                   return $this->connection;          } else {                      return false;          }    }}